const save = (k,v)=>{ try{localStorage.setItem(k,JSON.stringify(v));}catch(_){} };
const load = (k,fb=null)=>{ try{const v=localStorage.getItem(k);return v?JSON.parse(v):fb;}catch(_){return fb;} };
const todayStr  = ()=>new Date().toISOString().split("T")[0];
const weekStart = ()=>{const d=new Date(),diff=d.getDate()-d.getDay()+(d.getDay()===0?-6:1);return new Date(new Date().setDate(diff)).toISOString().split("T")[0];};
const getStreak  = ()=>load("lede_streak",{count:0,lastPosted:null,best:0,total:0});
const markPosted = ()=>{
  const s=getStreak(),today=todayStr();
  if(s.lastPosted===today)return s;
  const yest=new Date();yest.setDate(yest.getDate()-1);
  s.count=s.lastPosted===yest.toISOString().split("T")[0]?s.count+1:1;
  s.lastPosted=today;s.total=(s.total||0)+1;s.best=Math.max(s.count,s.best||0);
  save("lede_streak",s);return s;
};

